    window.sendTestAlert = function() {
        showMessage('ğŸ§ª Sending test alert to Slack...', 'info');

        const testSearch = '| makeresults | eval alert_text="ğŸ§ª *Test Alert from Splunk Dashboard*\\nTime: " + strftime(now(), "%Y-%m-%d %H:%M:%S") + "\\nMessage: Slack integration is working correctly!" | table alert_text | sendalert slack param.channel="C09DSD6JH2Q"';

